import React,{useState} from 'react'
import './Register.css';
export default function Register() {
    const [input,setinput]=useState('')

    const setRegister=(event)=>{

        const{name,value}=event.target
        setinput({
          ...input,[name]:value

        })
        console.log(input);
        }
  return (
   <>
   <form  >
             <div class='card'id="card1">
                <div class='card-body-center'id="card-body">
             
             <div className='fb'>
                <input type="text" placeholder='Name'name="name"value={input.name||""}onChange={setRegister} required id="in"/><br /><br />
                <input type="number" placeholder='contactnumber'name="number" value={input.number||""}onChange={setRegister}/><br/><br/>

                <input type="ema" placeholder='phone number'name="number"value={input.number||""}onChange={setRegister}  required id="in"/><br/><br/>
                <input type="" placeholder='password again 'name="passwordagain" value={input.password||""} onChange={setRegister}required id="in"/><br /><br />
                <input type="button" value="Sign Up" required id="btn"/><br />
                
             </div>
</div></div>
</form>
   </>
  )
}
