import React,{useState}from 'react'
import axios from 'axios'
export default function Login() {
  const [input,setinput]=useState('')
  
// const[postdata,setpostdata]=useState('')
  const setRegister=(event)=>{

      const{name,value}=event.target
      setinput({
        ...input,[name]:value

      })
      console.log(input);
      }
      const registerSubmit =(event)=>{
       event.preventDefault()
       console.log("hiii");
       
      axios.post('http://localhost:5000/api/login/logindata',input,
      {
        headers:{
        'Content-Type': 'application/json'
      }
    }
    )
      .then(response=>{
       console.log(response.data.message)
       const file=response.data.message
       console.log(file);
      //  const file=response.data.message
      //  console.log(file)

      }).catch((err)=>{
        console.log(err);
        console.log(err.response.data.message);
      })
      
      }
  return (
    
    <>
 <form onSubmit={registerSubmit}>
             <div class='card'id="card1">
                <div class='card-body-center'id="card-body">
             
             <div className='fb'>
                <input type="text" placeholder='User name'name="name"value={input.name||""}onChange={setRegister} required id="text"/><br /><br />
                 <input type="password" placeholder='password 'name="password" value={input.password||""} onChange={setRegister}required id="password"/><br /><br />
                <input type="submit" value="Login" required id="btn"/><br />
                
             </div>
</div></div>
</form>
  

</>
  )
}
