import React, { useState } from 'react'
import './Register.css';
import axios from 'axios'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function Register() {

  const [input, setinput] = useState({
    name: "",
    number: "",
    email: "",
    password: "",
    again: ""

  })
  const setRegister = (event) => {

    const { name, value } = event.target
    setinput({
      ...input, [name]: value

    })
    console.log(input);
  }
  const notify = (data) => toast(data);

  const registerSubmit = (event) => {
    event.preventDefault()
    if (input.name == ""||input.number==""||input.email==""||input.password==""||input.again=="") {
      console.log("hhhi");
      notify("please enter the data")
    }
   
    else {

      axios.post('http://localhost:5000/api/register', input,
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      )
        .then(response => {
          console.log(response.data.message)
          const file = response.data.message
          console.log(file);
        }).catch((err) => {
          console.log(err);
          console.log(err.response.data.message);
          notify(err.response.data.message)
        })

    }

  }
  return (
    <>
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />      
      
      <form onSubmit={registerSubmit} >
        <div class='card' id="card1">
          <div class='card-body-center' id="card-body">

            <div className='fb'>
              <input type="text" placeholder='Name' name="username" value={input.name || ""} onChange={setRegister}  id="text" /><br /><br />
              <input type="number" placeholder='password' name="password" value={input.number || ""} onChange={setRegister} id="number" /><br /><br />

              <input type="email" placeholder='name' name="name" value={input.email || ""} onChange={setRegister}  id="email" /><br /><br />
              <input type="password" placeholder='password ' name="address" value={input.password || ""} onChange={setRegister}  id="password" /><br /><br />
              <input type="password" placeholder='password again' name="phone" value={input.again || ""} onChange={setRegister}  id="password" /><br /><br />
              <input type="submit" value="Sign Up" required id="btn" /><br />

            </div>
          </div></div>
      </form>
    </>
  )
}
